import { useEffect, useState } from "react";
import axios from 'axios'

const WeatherCardBody = ({show, location}) => {
    const [result, setResult] = useState(null);
    const [err, setErr] = useState(false);
    
    useEffect(() => {
        if (location !== "") {
            console.log('fetching for \'',location,'\'');

            axios
                .get(`https://api.openweathermap.org/data/2.5/weather?q=${location}&units=metric&appid=439c9626324744899229e8a250853792`)
                .then(response => {
                    setResult(response.data);
                    setErr(false);
                    console.log(response.data);
                })
                .catch(e => {
                    setErr(true);
                    console.log(e);
                })
        }
    }, [location])
    
    if (err) {
        return (
            <div>Error</div>
        )
    } else {
        if (result)
            return (
                <div className="weather-container">
                    <div className="loc-name">{result.name}, {result.sys.country}</div>
                    <img src={`https://openweathermap.org/img/wn/${result.weather[0].icon}@2x.png`} alt={result.weather[0].description} />
                    <div className="weather-condition">{result.weather[0].description}</div>
                    <div className="temperature">
                        <i className="fa-solid fa-temperature-three-quarters"></i>
                        {result.main.temp}&#8451;
                        </div>
                </div>
            )
        return (
            <div>Not shown</div>
        )
    }
}

export default WeatherCardBody;
