import WeatherCardBody from "./WeatherCardBody";
import LocationFinder from "./LocationFinder";
import { useEffect, useState } from "react";

const WeatherCard = () => {
    const [showResult, setShowResult] = useState(false);
    const [location, setLocation] = useState("")

    useEffect(() => {
        console.log('fetching');
    }, [location])

    const handleSubmit = e => {
        e.preventDefault();

        const loc = e.target.location.value;

        if (loc !== "") {
            setLocation(loc);
            setShowResult(true);
        } else {
            alert("You can't leave the input empty!");
        }

        console.log('submitted', e.target.location.value);
    }
    
    return (
        <div className="container">
            <LocationFinder handleSubmit={handleSubmit}></LocationFinder>
            <WeatherCardBody show={showResult} location={location}></WeatherCardBody>
        </div>
    )
}

export default WeatherCard;
