    useEffect(() => {
        if (location !== "") {
            console.log('fetching for \'',location,'\'');

            axios
                .get(`https://api.openweathermap.org/data/2.5/weather?q=${location}&units=metric&appid=439c9626324744899229e8a250853792`)
                .then(response => {
                    setResult(response.data);
                    setErr(false);
                    console.log(result);
                })
                .catch(e => {
                    setErr(true);
                    console.log(e);
                })
        }
    }, [location])
    
    if (show) {
        return (
            <div>Shown</div>
        )
    }
